import React, { useState } from 'react';
import axios from 'axios';
import './signup-student.css'; // Import the CSS file

const Signup = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const [error, setError] = useState('');
  const [isSignedUp, setIsSignedUp] = useState(false);

  const handleSignup = async () => {
    try {
      const response = await axios.post('http://localhost:4000/signup', { email, password, role });
      console.log('Response:', response);
      setIsSignedUp(true);
    } catch (error) {
      console.error('Error:', error);
      setError(error.response?.data?.message || 'An error occurred');
    }
  };

  if (isSignedUp) {
    window.location.href = '/';
  }

  return (
    <div className="signup-container">
      <h1>Signup</h1>
      <div className="input-container">
        <input
          type="text"
          placeholder=" "
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="input-field"
        />
        <label>Email</label>
      </div>
      <div className="input-container">
        <input
          type="password"
          placeholder=" "
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="input-field"
        />
        <label>Password</label>
      </div>
      <div className="input-container">
        <select value={role} onChange={(e) => setRole(e.target.value)} className="select-field">
          <option value="">Select Role</option>
          <option value="Student">Student</option>
          <option value="Faculty">Faculty</option>
          <option value="HOD">HOD</option>
        </select>
      </div>
      <button onClick={handleSignup} className="button">Signup</button>
      {error && <p className="error-message">{error}</p>}
    </div>
  );
};

export default Signup;
