import React, { useState } from 'react';
import axios from 'axios';
import './signup-student.css'; // Import the CSS file

const SignupStudent = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [department, setDepartment] = useState('');
  const [year, setYear] = useState('');
  const [error, setError] = useState('');
  const [isSignedUp, setIsSignedUp] = useState(false);

  const handleSignup = async () => {
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      const response = await axios.post('http://localhost:4000/signup-student', { email, password, department, year });
      console.log('Response:', response);
      setIsSignedUp(true);
    } catch (error) {
      console.error('Error:', error);
      setError(error.response?.data?.message || 'An error occurred');
    }
  };

  if (isSignedUp) {
    window.location.href = '/';
  }

  return (
    <div>
      <h1>Signup</h1>
      <div>
        <input
          type="text"
          placeholder="E-Mail "
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="input-field"
        />
      </div>
      <div>
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="input-field"
        />
      </div>
      <div>
        <input
          type="password"
          placeholder="Confirm Password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          className="input-field"
        />
      </div>
      <div>
        <label>Select Department:</label>
        <div>
          <input
            type="radio"
            name="department"
            value="IT"
            checked={department === 'IT'}
            onChange={() => setDepartment('IT')}
          />
          <label>IT</label>
        </div>
        <div>
          <input
            type="radio"
            name="department"
            value="CSE"
            checked={department === 'CSE'}
            onChange={() => setDepartment('CSE')}
          />
          <label>CSE</label>
        </div>
        <div>
          <input
            type="radio"
            name="department"
            value="ECE"
            checked={department === 'ECE'}
            onChange={() => setDepartment('ECE')}
          />
          <label>ECE</label>
        </div>
        <div>
          <input
            type="radio"
            name="department"
            value="EEE"
            checked={department === 'EEE'}
            onChange={() => setDepartment('EEE')}
          />
          <label>EEE</label>
        </div>
        <div>
          <input
            type="radio"
            name="department"
            value="MECH"
            checked={department === 'MECH'}
            onChange={() => setDepartment('MECH')}
          />
          <label>MECH</label>
        </div>
        <div>
          <input
            type="radio"
            name="department"
            value="AI&DS"
            checked={department === 'AI&DS'}
            onChange={() => setDepartment('AI&DS')}
          />
          <label>AI&DS</label>
        </div>
        {/* Add more departments as needed */}
      </div>
      <div>
        <label>Select Year:</label>
        <div>
          <input
            type="radio"
            name="year"
            value="1"
            checked={year === '1'}
            onChange={() => setYear('1')}
          />
          <label>1</label>
        </div>
        <div>
          <input
            type="radio"
            name="year"
            value="2"
            checked={year === '2'}
            onChange={() => setYear('2')}
          />
          <label>2</label>
        </div>
        <div>
          <input
            type="radio"
            name="year"
            value="3"
            checked={year === '3'}
            onChange={() => setYear('3')}
          />
          <label>3</label>
        </div>
        <div>
          <input
            type="radio"
            name="year"
            value="4"
            checked={year === '4'}
            onChange={() => setYear('4')}
          />
          <label>4</label>
        </div>
        {/* Add more years as needed */}
      </div>
      <button onClick={handleSignup}>Signup</button>
      {error && <p className="error-message">{error}</p>}
    </div>
  );
};

export default SignupStudent;
